module.exports = function(ngModule) {

  ngModule.filter('truncate', function () {
    return function (text, length, end) {

      if (text === undefined) {
        text = '';
      };

      if (isNaN(length)){
        length = 10;
      }

      if (end === undefined){
        end = '...';
      }

      if (text.length <= length || text.length - end.length <= length) {
        return text;
      } else {
        return String(text).substring(0, length-end.length) + end;
      }

    };
  });
};
