    var allowedRoles = note.permittedUsers || [];
    var isAllowed = false;

    // that means the note is private
    if(!allowedRoles.length) {

      allowedRoles.push('Private');

    }

    var allowedUserRoles = [];

    var len = allowedRoles.length;
    var index;

    for(index=0;index<len;index++) {

      var currRole = allowedRoles[index];

      var associatedRoles = getAssociatedRoles(currRole,appType);

      // join the array in the list of user roles that have the access
      allowedUserRoles = allowedUserRoles.concat(associatedRoles);

    }

    var allowedIndex = allowedUserRoles.indexOf(userRole);

    if(allowedIndex < 0) {
      return isAllowed;
    }

    isAllowed = true;
    return isAllowed;    
