  /**
   * Process is to be the base object of any process (i.e. Consults) to provide convenience methods such as getNext.
   *
   * @param {Array} steps An array of objects
   *                      {
   *                        label : 'label of step',
   *                        state : 'correlating state name of step'
   *                      }
   */
  function Process ( steps ){

    this.steps = initalizeSteps(steps);

    if(!this.status){
      this.current();
    }

  };


  function initalizeSteps ( steps ){

    var length = steps.length;

    _.map( steps, function(step, index){
      initalizeStep(step, index, length);
    });

    return steps;

  };


  function initalizeStep ( step, index, length ){

    step.step = index + 1;
    step.of = length;

    step.progress = (index + 1) / length;

    step.isFirst = (index === 0);
    step.isLast = (step.progress === 1);

    if(!step.label){
      step.label = step.state;
    }
  };


  Process.prototype.setStep = function setStep (step){
    this.status = step.state;
  };


  Process.prototype.isStep = function isStep (step){

    return _.findIndex(this.steps, {state : step}) > -1;

  };

  Process.prototype.isAllowedStep = function allowedStep (step){

    var stepth = _.find(this.steps, {state : step});
    var currentStep = this.getCurrentStep();

    return currentStep.step >= stepth.step;
  };

  Process.prototype.getStep = function getStep (index){

    var step = this.steps[index];


    if(!step){
      step = this.steps[0];
      // return angular.copy(step);
      // log that step does not exist
    }

    // return first step if step does not exist.
    return angular.copy(step);
  };

  Process.prototype.getFirstStep = function getFirstStep (){

    var step = this.steps[0];

    return step;

  };


  Process.prototype.getCurrentStep = function getCurrentStep ( currentStep , type ){

    var currentStepObj = {};
    var currentIndex;

    if(!_.isUndefined(this.status)){
      currentStep = this.status;
    }

    if (_.isUndefined(currentStep)){
      return this.getStep(0);
    }

    if (_.isObject(currentStep)){

      currentStepObj = currentStep;

    } else {

      if (_.isUndefined(type)){
        type = 'label';
      }

      currentStepObj[type] = currentStep;

    }

    currentIndex = _.findIndex( this.steps, currentStepObj );
    return this.getStep(currentIndex);

  };

  Process.prototype.current = function current (){
    var CurrentStep = this.getCurrentStep();

    return this.getCurrentStep();
  };

  Process.prototype.getNextStep = function getNextStep ( currentStep , type ){

    var currentStepObj = this.getCurrentStep( currentStep, type );

    if(currentStepObj.step == 12) this.finish();
    if(this.reachedFinal && currentStepObj.step !== 12) return this.getStep(11);
    // a little bit tricky -- step is n + 1
    return this.getStep(currentStepObj.step);

  };

  Process.prototype.finish = function() {
    this.reachedFinal = true;
  }

  Process.prototype.getPreviousStep = function getPreviousStep ( currentStep , type ){

    var currentStepObj = this.getCurrentStep( currentStep, type );

    // a little bit tricky -- step is n + 1
    return this.getStep(currentStepObj.step - 2);

  };

