    $scope.missed = [];
    $scope.book = {
      day : (new Date()).toDateString()
    };

    $scope.hideUpcoming = false;

    var today = new Date();
    today.setHours(0);
    today.setMinutes(0);
    today.setSeconds(0);
    today.setMilliseconds(0);
    today = today.getTime();

