  function RequestService($state, $rootScope, ProcessService, appConfig, $ionicLoading) {
    
    var service = {};

    window.listenEvent('removeLoading', function() {
      window.stillOffline = true;
      $ionicLoading.hide();
    });    

    var stepsList = [
      { state : 'mobileMD.request' },
      { state : 'mobileMD.request.members' },
      { state : 'mobileMD.request.doctors' },
      { state : 'mobileMD.request.verification' },
      { state : 'mobileMD.home' }
    ];

    $rootScope.showLoadingText = function() {
      $ionicLoading.show({
        template: 'Loading...',
        animation: 'fade-in',
        //showDelay: 300,
        showBackdrop: true
      });
    };
    
    $rootScope.hideLoadingText = function(){
      $ionicLoading.hide();
    };

    // Setup the steps based on the step list. This will allow you to getNextStep and many other features.
    service.steps = new ProcessService(stepsList);

    // This sets the step status to the current $state if in the stepList
    if (service.steps.isStep($state.current.name)) {
      service.steps.status = $state.current.name;
    }

    $rootScope.$on('$stateChangeStart', function(event, toState, toParams, fromState, fromParams) {
      
      if(window.stillOffline) {
        window.emit('removeLoading');
        window.emit('showError');
      }

    });

    // Any time the state changes it will check to see if the state is in the stepList and if so update the step status.
    $rootScope.$on('$stateChangeSuccess', function(event, toState, toParams, fromState, fromParams){
      
      if (service.steps.isStep(toState.name)) {
        service.steps.status = toState.name;
      }
    
    });

    service.newSchema = {
      user: { },
      guests: [ ],
      symptoms: [],
      affectedArea: [],
      stepsFinalReview: false
    };

    // Gets the symptoms list from the appConfig, prepares it for the UI list, and pushes it to the request.symptoms array.
    _.forEach(appConfig.metadata.symptoms, function(item){
      service.newSchema.symptoms.push({"text":item ,"enabled": false });
    });

    // Gets the affectedArea list from the appConfig, prepares it for the UI list, and pushes it to the request.affectedArea array.
    _.forEach(appConfig.metadata.affectedArea, function(item){
      service.newSchema.affectedArea.push({"text":item ,"enabled": false });
    });


    // Go to one forward or final step
    service.steps.goNextStepOrFinal = function(){
      if (!service.schema.stepsFinalReview) {
        return $state.go(service.steps.getNextStep().state);
      } else {
        return $state.go('mobileMD.request.verification');
      }
    };

    // Go two steps forward or final step
    service.steps.goNextNextStepOrFinal = function(){
      if (!service.schema.stepsFinalReview) {
        service.steps.setStep(service.steps.getNextStep())
        return $state.go(service.steps.getNextStep().state);
      } else {
        return $state.go('mobileMD.request.verification');
      }
    };

    // Go to one forward
    service.steps.goNextStep = function(){
      return $state.go(service.steps.getNextStep().state);
    };

    // Go to steps forward
    service.steps.goNextNextStep = function(){
      service.steps.setStep(service.steps.getNextStep())
      return $state.go(service.steps.getNextStep().state);
    };

    service.clearSchema = function(){
      service.schema = angular.copy(service.newSchema);
    };

    service.clearSchema();

    return service;

  }

