  function UploadCtrl($scope, $state, CameraAccess, Restangular, appConfig, $ionicPopup) {
   
    $scope.imagesList = [];
    $scope.request.uploads = $scope.request.uploads || [] ;
    $scope.loadingArr  = [];
    $scope.loadingIndex = 0;

    if($scope.request.uploads) {
      
      _.forEach($scope.request.uploads, function(imageId) {

        var uploadUrl = 'uploads/'+ imageId;

        Restangular.one(uploadUrl).get().then(function(response) {


          if(response) {        

            var uri = {
              url : appConfig.apiURL + "/" + response.location,
              loading : 100,
              imageId: response.id
            };        

            $scope.imagesList.push(uri);
          }

        });
      });
    }
      
    $scope.getPhoto = function() {

      var options = {
        quality: 75,
        targetHeight: 400,
        targetWidth: 400,
        destinationType: Camera.DestinationType.FILE_URI,
        sourceType: Camera.PictureSourceType.CAMERA
      };

      CameraAccess.getPicture(options).then(function(imageURI) {

        var uri = {
          url : imageURI,
          loading : 0
        };

        $scope.imagesList.push(uri);
        var currIndex = $scope.imagesList.length - 1;

        $scope.uploadImagesToServer(uri,currIndex);     
        
      }, function(err) {
        console.log(err);
      });

    };

    $scope.getPhotoFromAlbum = function() {

      var options = {
        quality: 75,
        destinationType: Camera.DestinationType.FILE_URI,
        sourceType: Camera.PictureSourceType.SAVEDPHOTOALBUM,
        maximumImagesCount: 10,
        width: 1024
      };

      CameraAccess.getPictureFromGallery(options).then(function(results) {

        for (var i = 0; i < results.length; i++) {

          var uri = {
            url : results[i],
            loading : 0
          };

          $scope.imagesList.push(uri);  
          var currIndex = $scope.imagesList.length - 1;

          $scope.uploadImagesToServer(uri, currIndex);  
        }       

      }, function(err) {
        console.log(err);
      }); 

    };  


    $scope.uploadImageSuccess = function(r) { 
      
      var res = JSON.parse(r.response);
      
      $scope.request.uploads.push(res[0]._id);  

      var currIndex = $scope.loadingArr[0];

      // set the uploaded image loading percent to 100
      $scope.imagesList[currIndex]['loading'] = 100;

      // append the id of the image here
      $scope.imagesList[currIndex]['imageId'] = res[0]._id;
      
      $scope.loadingArr.splice(0,1);

      $scope.$apply();

    }; 

    $scope.uploadImageFail = function(error) { 

      $ionicPopup.alert({
          title: "Internet Disconnected",
          content: "connection lost. please try uploading this photo from a wi-fi connection."
      })
      .then(function(result) {

          if(result) {

            $state.go('mobileMD.request.verification');
          }
      });

    }; 

    $scope.uploadImagesToServer = function(imgObj, index) {

      var imageURI = imgObj.url;
      var options = new FileUploadOptions();
      var loadingStatus = 0;
      var serverUrl = encodeURI(appConfig.apiURL+"/uploads/images");
      options.fileKey = "fileUpload"; 
      options.mimeType = "image/jpeg";
      options.chunkedMode = false;

      $scope.loadingArr.push(index);

      //Camera Access service for the upload
      var ft = CameraAccess.upload(imageURI, serverUrl, $scope.uploadImageSuccess, $scope.uploadImageFail, options);
 
       ft.onprogress = function(progressEvent) {       

        if (progressEvent.lengthComputable) {

          var progressPerc = Math.floor(progressEvent.loaded / progressEvent.total * 100);

          if(progressPerc >= 90) {

            imgObj.loading = 90;
            
            $scope.$apply();

            return;

          }

          imgObj.loading = progressPerc;

          $scope.$apply();

        } else {
          
          loadingStatus.increment();
        }

      };
    };

    $scope.deleteImage = function (photo) {

      var id = photo.imageId;
      var url = 'uploads/' + id + '/delete';

      Restangular.all(url).post().then(function(response) {

        if(response) {

          $scope.request.uploads.splice($scope.request.uploads.indexOf(id), 1);
          $scope.imagesList.splice($scope.imagesList.indexOf(photo), 1);
        }  

      },function(err) {

        console.log(err);
      });     
    }

  }

